if (null != document.querySelector("#block")) {
  const e = document.querySelector("#block").style.flexWrap;
  function mobile(l) {
    l.matches
      ? ((document.querySelector("#block").style.flexWrap = "wrap"),
        (document.querySelector("#block").style.backgroundImage =
          "url('/assets/img/bg.jpg')"),(document.querySelector("#bg-rem").style.background = null),
        (document.querySelector("#image").style.display = "none"))
      : ((document.querySelector("#block").style.flexWrap = e),
        (document.querySelector("#block").style.backgroundImage = ""),(document.querySelector("#bg-rem").style.background = 'url("../assets/img/bg.jpg")'),
        (document.querySelector("#image").style.display = "flex"));
  }
  var x = window.matchMedia("(max-width: 50em)");
  mobile(x), x.addListener(mobile);
}
